  color: #fff;
  position: relative;
}

.footer {
  position: fixed;
  left: 0;
  bottom: 0;
  width: 100%;
  height: 80px;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 50;
}
.footer__svg--desktop {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
