  position: relative;
  font-size: 1.2rem;
  text-transform: uppercase;
  color: #3d1f83;
  border: 3px solid #3d1f83;
  border-radius: 50px;
  font-weight: 700;
  padding: 0.5rem 2rem;
  background: #fff;
  transition: 0.2s ease-in;
  cursor: pointer;
}
.footer .btn:active {
  transform: scale(0.98);
}

.main {
  position: relative;
  display: flex;
  flex-flow: column;
  flex: 1;
  margin: 1rem auto 0;
  width: 100%;
  max-width: 768px;
  overflow: hidden;
}
