@media (min-width: 768px) {
  .tabs__list {
    border: none;
  }
}
.tabs__item {
  flex: 1;
  background: #fff;
  text-align: center;
  color: #3d1f83;
  font-size: 1.2rem;
  cursor: pointer;
  transition: 0.4s ease;
}
.tabs__item.active {
  background: #3d1f83;
}
.tabs__item.active .tabs__item__content {
  color: #3d1f83;
  background: #fff;
  font-weight: 700;
  border-top-left-radius: 10px;
  border-top-right-radius: 10px;
}
@media (min-width: 768px) {
  .tabs__item--first {
    border-top-left-radius: 8px;
  }
}
.tabs__item--first .tabs__item__content {
  border-top-right-radius: 0;
