    border-top-right-radius: 8px;
  }
}
.tabs__item--third .tabs__item__content {
  border-top-left-radius: 0;
}
@media (min-width: 768px) {
  .tabs__item--third .tabs__item__content {
    border-top-right-radius: 8px;
  }
}
.tabs__item__content {
  color: #fff;
  background: #3d1f83;
  display: block;
  padding: 0.4rem 0;
  transition: 0.4s ease;
}

.content {
  position: absolute;
  flex: 1;
  left: 0;
  bottom: 20px;
  z-index: 0;
  width: 100%;
  height: calc(100% - 60px);
  padding: 1rem 1rem 80px;
  opacity: 0;
  overflow-y: scroll;
  scrollbar-color: lightgrey transparent;
  scrollbar-width: thin;
  transition: 0.4s ease;
}
.content.show {
  opacity: 1;
  bottom: 0;
  z-index: 40;
}
.content__item__title {
  font-size: 1.2rem;
  color: #3d1f83;
  text-transform: uppercase;
  font-weight: 700;
  margin: 1rem 0;
}
.content .input-checkbox {
  visibility: hidden;
  position: absolute;
  top: 0;
  left: 0;
}
.content .input-checkbox:checked + .label {
  font-weight: 700;
}
.content .input-checkbox:checked + .label .checkbox {
  background: #3d1f83;
}
.content .label {
  display: flex;
  align-items: center;
  margin: 0 0 0.5rem;
  color: #3d1f83;
  transition: 0.2s ease;
  cursor: pointer;
}
.content .label .checkbox {
  width: 24px;
  height: 24px;
  border: 2px solid #3d1f83;
  border-radius: 4px;
  background: #fff;
  margin: 0 1rem 0 0;
  transition: 0.2s ease;
  display: flex;
  justify-content: center;
  align-items: center;
}

.content::-webkit-scrollbar-track {
  -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
  background-color: transparent;
}

.content::-webkit-scrollbar {
  width: 6px;
  background-color: transparent;
}

.content::-webkit-scrollbar-thumb {
  background-color: lightgray;
}

