$font-size-sm: 0.8rem;
$font-size-xs: 0.6rem;

////////////////
// FONT WEIGHT
////////////////

$fontWeightThin: 300;
$fontWeightBase: 400;
$fontWeightMedium: 500;
$fontWeightSemi: 600;
