///////////
// BUTTON
///////////
button,
.btn {
  outline: none;
  border: 0;
}

// CANCELS OUTLINE INPUT
input,
button,
select,
textarea {
  &:focus {
    outline: none;
  }
}

///////////
// CLASSES
///////////
.hidden {
  display: none !important;
}

.mobile-hidden {
  display: none !important;

  @include screen-sm() {
    display: block !important;
  }
}

.desktop-hidden {
  display: block !important;

  @include screen-sm() {
    display: none !important;
  }
}

.invisible {
  visibility: hidden !important;
}

//////////
// HACKS
//////////

// CANCELS RED BORDER ON FIREFOX INPUT ERROR
textarea,
input {
  &:invalid {
    box-shadow: none;
  }
}

//CANCELS SAFARI INPUT ICON
input::-webkit-contacts-auto-fill-button {
  visibility: hidden;
  display: none !important;
  pointer-events: none;
  position: absolute;
  right: 0;
}

input:focus::-webkit-contacts-auto-fill-button {
  visibility: hidden;
  display: none !important;
  pointer-events: none;
  position: absolute;
  right: 0;
}

//CANCELS INTERNET EXPLORER AND EDGE CLEAR INPUT ICON
input::-ms-clear {
  display: none;
}

//CANCELS THE YELLOW BACKGROUND COLOR ON INPUT AUTOFILL ON CHROME
input:-webkit-autofill,
input:-webkit-autofill:hover,
input:-webkit-autofill:focus,
textarea:-webkit-autofill,
textarea:-webkit-autofill:hover,
textarea:-webkit-autofill:focus,
select:-webkit-autofill,
select:-webkit-autofill:hover,
select:-webkit-autofill:focus {
  -webkit-box-shadow: 0 0 0px 1000px transparent inset;
  transition: background-color 5000s ease-in-out 0s;
}

